import React from "react";
import { Link } from "react-router-dom";

class BookingForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      pickuplocation: [
        { id: 1, name: "Colombo" },
        { id: 2, name: "Katunayeke Airport" },
        { id: 3, name: "Galle" },
        { id: 4, name: "Kandy" },
      ],
      returnlocation: [
        { id: 1, name: "Colombo" },
        { id: 2, name: "Katunayeke Airport" },
        { id: 3, name: "Galle" },
        { id: 4, name: "Kandy" },
      ],

      schedules: [],
      pickUpDate: "",
      dropOffDate: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  // componentDidMount(){
  //     fetch('http://localhost:8081/api/getSchedules')
  //     .then(res=>res.json())
  //     .then((data)=>{
  //         this.setState({schedules : data})
  //     }).catch(console.log)

  // }
  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({
      [name]: value,
    });
  }
  handleSubmit(event) {
    event.preventDefault();

    alert(this.state.pickUpDate + "/" + this.state.dropOffDate);
    // const data = new FormData(event.target);

    // fetch('/Results/',{
    //     method:'POST',
    //     body:data,
    // });
  }

  render() {
    return (
      <div className="bookingbody">
        <div className="bookingSec">
          <form className="bookingForm" onSubmit={this.handleSubmit}>
            <label>
              Pick-Up Date :
              <input
                type="date"
                name="pickUpDate"
                value={this.state.pickUpDate}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Drop-Off Date :
              <input
                type="date"
                name="dropOffDate"
                value={this.state.dropOffDate}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Pick-Up Location :
              <div>
                <select
                  id="selectComponent"
                  value={this.state.value}
                  onChange={this.handleInputChange}
                >
                  {this.pickuplocation.map((p) => (
                    <option key={p.id} value={p.name}>
                      {p.name}
                    </option>
                  ))}
                </select>
              </div>
            </label>
            <label>
              Return Location :
              <div>
                <select
                  id="selectComponent"
                  onChange={props.activity}
                >
                  {this.returnlocation.map((p) => (
                    <option key={p.id} value={p.name}>
                      {p.name}
                    </option>
                  ))}
                </select>
              </div>
            </label>

            <input type="submit" value="Find" onClick={this.onSubmit} />
          </form>
        </div>
      </div>
    );
  }
}

export default BookingForm;
