import React from "react";
import { Link } from "react-router-dom";

class BookingForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      schedules: [],
      location:[
          {value : "Galle",label:'Galle'},
          {value : "Galle",label:'Galle'},
          {value : "Galle",label:'Galle'},
          {value : "Galle",label:'Galle'},
      ],
      pickUpDate: "",
      dropOffDate: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

 
  // componentDidMount(){
  //     fetch('http://localhost:8081/api/getSchedules')
  //     .then(res=>res.json())
  //     .then((data)=>{
  //         this.setState({schedules : data})
  //     }).catch(console.log)

  // }
  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({
      [name]: value,
    });
  }
  handleSubmit(event) {
    event.preventDefault();

    alert(this.state.pickUpDate + "/" + this.state.dropOffDate);
    // const data = new FormData(event.target);

    // fetch('/Results/',{
    //     method:'POST',
    //     body:data,
    // });
  }

  render() {
    return (
      <div className="bookingbody">
        <div className="bookingSec">
          <form className="bookingForm" onSubmit={this.handleSubmit}>
            <label>
              Pick-Up Date :
              <input
                type="date"
                name="pickUpDate"
                value={this.state.pickUpDate}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Drop-Off Date :
              <input
                type="date"
                name="dropOffDate"
                value={this.state.dropOffDate}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Pick-Up Location :
             <select value={this.state.value} onchange={this.handleInputChange}>
                 <option value="Galle">Galle</option>
                 <option value="Colombo">Colombo</option>
                 <option value="Kandy">Kandy</option>
                 <option value="Negombo">Negombo</option>
             </select>
            </label>
            <label>
              Return Location :
              <select value={this.state.value} onchange={this.handleInputChange}>
                 <option value="Galle">Galle</option>
                 <option value="Colombo">Colombo</option>
                 <option value="Kandy">Kandy</option>
                 <option value="Negombo">Negombo</option>
             </select>
            </label>

            <input type="submit" value="Find" onClick={this.onSubmit} />
          </form>
        </div>
      </div>
    );
  }
}

export default BookingForm;
