import React from 'react';
import NavBar from './Nav';
import CarCard from './CarCard';
import MotorBikeCard from './MotorBikeCard';

import Container from 'react-bootstrap/Container';

class Vehicles extends React.Component{
    constructor(props){
        super(props);
        this.state={
          vehicles:[]
        }
      }
      componentDidMount(){
        fetch('http://localhost:8081/api/get')
        .then(res=>res.json())
        .then((data)=>{
            this.setState({vehicles : data})
            console.log(data)
        }).catch(console.log) 
    }
      render(){ 
        return(
          <Container>
                <VehicleItem vehicles={this.state.vehicles}/>
          </Container>
     
        );
      
      }

}
//Items are displayed in a two column grid
const Items = () => {
  const rows = [...Array(Math.ceil(items.length / 2))];
  const itemRows = rows.map((row, i) => items.slice(i * 2, i * 2 + 2));
  const content = itemRows.map((row, i) => (
    <div className="row px-5 pb-4" key={i}>
      {row.map((item) => (
        <div
        className="col mx-lg-5 mx-md-0 mx-sm-0 border-bottom border-secondary"
          key={item.appId}
        >
          
          <SearchBox
            id={item.appId}
            title={item.newTitle}
            developer={item.developer}
            summary={item.summary}
            icon={item.icon}
            installs={item.installs}
            rating={item.rating}
            price={item.price}
          />
        </div>
      ))}
    </div>
  ));
  return <div>{content}</div>;
};
const VehicleItem = ({vehicles})=>{
    return(
        <Container>
            <h1><center>VEHICLE LIST</center></h1>
            <React.Fragment>
            {vehicles.map((item)=>{
             const Type = (item.vehicle_TYPE==='Car'? CarCard : MotorBikeCard);
             console.log(Type);
            
             
            return(
            <Type data={item}/>
            );
          })}
            </React.Fragment>
            
       
        </Container>
    );

}
export default  Vehicles;
